import React, { useEffect, useState } from "react";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
import axios from "axios";
import Slider from "react-slick";

import formatDate from "../../hook/FormatDate";

export default function OtherTopics() {
  const [postInTab, setPostInTab] = useState([]);
  const baseURL = "http://127.0.0.1:8000/";

  const fetchTags = async () => {
    const response = await axios.get(`${baseURL}api/Tabstags`);
    return response.data.tags;
  };

  const {
    data: tags,
    isLoading: isLoadingTags,
    isError: isTagsError,
  } = useQuery("tags", fetchTags);

  useEffect(() => {
    if (tags && tags.length > 0) {
      setPostInTab(tags[0].posts);
    } else {
      setPostInTab([]);
    }
  }, [tags]);

  const handleTagClick = (tag) => {
    setPostInTab(tag.posts);
  };

  const renderTags = () => {
    return tags.map((tag) => (
      <li key={tag.id}>
        <Link
          to={`/tag/${tag.id}`}
          onClick={() => handleTagClick(tag)}
          title={tag.TITLE}
        >
          {tag.TITLE}
        </Link>
      </li>
    ));
  };

  const renderPosts = () => {
    return postInTab.slice(0, 6).map((post, index) => (
      <li key={post.id} className="post">
        <Link to={`/post/${post.id}`} title={post.TITLE}>
          <img src={baseURL + post.Main_IMG} alt={post.TITLE} />
        </Link>
        <h5>
          <span className="number">{index + 1}.</span>
          <Link to={`/post/${post.id}`} title={post.TITLE}>
            {post.TITLE}
          </Link>
        </h5>
        <ul className="post_details simple">
          <li className="category">
            <Link to={`/tag/${post.tag.id}`} title={post.tag.TITLE}>
              {post.tag.TITLE}
            </Link>
          </li>
          <li className="date">{formatDate(post.DATE)}</li>
        </ul>
      </li>
    ));
  };

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  return (
    <div className="row page_margin_top_section">
      <h4 className="box_header">مواضيع أخرى</h4>
      <div className="tabs no_scroll margin_top_10 clearfix">
        <ul className="tabs_navigation small clearfix">
          {isLoadingTags ? (
            <li>Loading...</li>
          ) : isTagsError ? (
            <li>Error fetching tags</li>
          ) : (
            renderTags()
          )}
        </ul>

        <div className="horizontal_carousel_container page_margin_top">
          <Slider {...sliderSettings}>{renderPosts()}</Slider>
        </div>
      </div>
    </div>
  );
}
