import React, { useEffect, useState } from "react";
import { useQuery } from "react-query";
import axios from "axios";
import { Link } from "react-router-dom";
export default function CheckIfHaveTag({ child }) {

      const [childtags, setChildTags] = useState(null);
      const [isLoading, setIsLoading] = useState(true);
      const [isError, setIsError] = useState(false);
    
      const fetchChildTagsData = async () => {
        try {
          const response = await axios.get(`http://127.0.0.1:8000/api/childtags/${child.id}`);
          setChildTags(response.data);
          setIsLoading(false);
          setIsError(false);
        } catch (error) {
          setIsError(true);
          setIsLoading(false);
        }
      };
    
      useEffect(() => {
 
        const intervalId = setInterval(() => {
        }, 2000);
    
        // Fetch data immediately when the component mounts
        fetchChildTagsData();
    
        return () => {
          clearInterval(intervalId); 
        };
      }, [child]);
    
      if (isError || isLoading) {
        return null;
      }
    
      return (
        <>
          {childtags && childtags.children && (
            <ul className="animated fadeIn">
              {childtags.children.map((childTag) => (
                <>
                <li className="flyout-left" key={childTag.id}>
                  <Link to={`/tag/${childTag.TITLE}`}>{childTag.TITLE}</Link>
                </li>
                </>
              ))}
            </ul>
          )}
        </>
      );
    }
